    private void update()
    {
        try
        {
            if(jTextField2.getText().trim().equals(jTextField3.getText().trim()))
            {
                String qry="DELETE FROM Account where UserID='"+jTextField1.getText().trim()+"' and UserPass='"+jTextField2.getText().trim()+"'";
                boolean bn=Utility.saveInformation(qry);
                if(bn==true)
                {
                    javax.swing.JOptionPane.showMessageDialog(this, "Congratulations! user has been Deleted");
                }
            }
            else
            {
                javax.swing.JOptionPane.showMessageDialog(this,"Entered password and confirm password not matched");
                jTextField2.setText("");
                jTextField3.setText("");
                jTextField2.requestFocus();
            }
        }
        catch(Exception ae)
        {
            System.out.println("Eror............"+ae);
        }
    }
